INSERT INTO custom_reports (id, name, description, sql_query, chart_config, parameters, category, is_shared, created_by, created_at, updated_at, active) VALUES (1, 'Sales by Month', 'Monthly sales revenue analysis', 'SELECT DATE_TRUNC(''month'', order_date) as month, SUM(total_amount) as revenue FROM sales_orders WHERE order_date >= NOW() - INTERVAL ''12 months'' GROUP BY month ORDER BY month', [object Object], , 'sales', false, 'system', '2025-06-04T15:23:51.126Z', '2025-06-04T15:23:51.126Z', true);
INSERT INTO custom_reports (id, name, description, sql_query, chart_config, parameters, category, is_shared, created_by, created_at, updated_at, active) VALUES (2, 'Top Customers by Revenue', 'Customers with highest revenue contribution', 'SELECT c.name as customer_name, SUM(so.total_amount) as total_revenue FROM customers c JOIN sales_orders so ON c.id = so.customer_id GROUP BY c.id, c.name ORDER BY total_revenue DESC LIMIT 10', [object Object], , 'sales', false, 'system', '2025-06-04T15:23:51.126Z', '2025-06-04T15:23:51.126Z', true);
INSERT INTO custom_reports (id, name, description, sql_query, chart_config, parameters, category, is_shared, created_by, created_at, updated_at, active) VALUES (3, 'Inventory Levels by Category', 'Current inventory levels grouped by material category', 'SELECT cat.name as category, SUM(inv.quantity) as total_quantity FROM inventory inv JOIN materials m ON inv.material_id = m.id JOIN categories cat ON m.category_id = cat.id GROUP BY cat.id, cat.name ORDER BY total_quantity DESC', [object Object], , 'inventory', false, 'system', '2025-06-04T15:23:51.126Z', '2025-06-04T15:23:51.126Z', true);
INSERT INTO custom_reports (id, name, description, sql_query, chart_config, parameters, category, is_shared, created_by, created_at, updated_at, active) VALUES (4, 'Cost Center Expenses', 'Expenses by cost center for current year', 'SELECT cc.name as cost_center, SUM(e.amount) as total_expenses FROM cost_centers cc LEFT JOIN expenses e ON cc.id = e.cost_center_id WHERE EXTRACT(YEAR FROM e.expense_date) = EXTRACT(YEAR FROM NOW()) GROUP BY cc.id, cc.name ORDER BY total_expenses DESC', [object Object], , 'finance', false, 'system', '2025-06-04T15:23:51.126Z', '2025-06-04T15:23:51.126Z', true);
INSERT INTO custom_reports (id, name, description, sql_query, chart_config, parameters, category, is_shared, created_by, created_at, updated_at, active) VALUES (5, 'Purchase Orders by Vendor', 'Purchase order volumes by vendor', 'SELECT v.name as vendor_name, COUNT(po.id) as order_count, SUM(po.total_amount) as total_amount FROM vendors v JOIN purchase_orders po ON v.id = po.vendor_id GROUP BY v.id, v.name ORDER BY total_amount DESC LIMIT 15', [object Object], , 'purchase', false, 'system', '2025-06-04T15:23:51.126Z', '2025-06-04T15:23:51.126Z', true);
